package ch02;

public class ConstantTest {

	// 메인함수 (코드의 시작점)
	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		// 상수(constant)
		
		final int MAX_NUM = 12; // 상수를 선언과 초괴화를 동시에
		final int MIN_NUM; // 상수 선언
		
		MIN_NUM = 1;
		
		System.out.println(MIN_NUM);
		System.out.println(MAX_NUM);
		
		//MAX_NUM = 100;  상수에 한번 초기화 된 공간에 값을 변경할 수 없다.
		
		final int MY_COUNT = 100; // 상수는 대문자 와 _ 로 구분지어줌
		
		// 상수는 = MY_CONTH
		// 예약어는 final , int
		// 100 = 리터럴(값)
		
		
		
		
		
		
		
		
		
		
		
		
		

	} // end of main

} // end of class
